import React, { useRef, useEffect } from "react";
import { io } from "socket.io-client";
import Peer from "peerjs";
import {
  MediaPlayer,
  MediaOutlet,
  MediaCommunitySkin,
} from "@vidstack/react";

export default function Stream() {
  const socket = io("http://localhost:5000");
  const myVideoRef = useRef();

  let roomid = 1;
  const peerRef = useRef();

  const initPeer = () => {
    const peerInstance = new Peer();

    peerRef.current = peerInstance;

    peerInstance.on("open", (peerId) => {
      console.log("Connected to PeerJS with ID:", peerId);
      socket.emit("broadcaster", roomid, peerId);
    });

    navigator.mediaDevices
      .getUserMedia({ audio: true, video: true })
      .then((stream) => {
        myVideoRef.current.srcObject = stream;
        console.log("Stream obtained successfully:", stream);
      })
      .catch((error) => console.error("Error accessing media devices:", error));

    peerInstance.on("call", (call) => {
      console.log("Incoming call:", call);

      // Answer the incoming call and send our stream
      call.answer(myVideoRef.current.srcObject);

      call.on("stream", (remoteStream) => {
        // Handle the remote stream as needed
      });
    });
  };

  useEffect(() => {
    initPeer();

    return () => {
      // Cleanup code (e.g., disconnect from the socket, close peer connections)
    };
  }, []);

  return (
    <div>
      <MediaPlayer
        title="Sprite Fight"
        poster="https://image.mux.com/VZtzUzGRv02OhRnZCxcNg49OilvolTqdnFLEqBsTwaxU/thumbnail.webp?time=268&width=980"
        thumbnails="https://media-files.vidstack.io/sprite-fight/thumbnails.vtt"
        aspectRatio={16 / 9}
        autoplay={true}
        crossorigin=""
        ref={myVideoRef}
      >
        <MediaOutlet>
          {/* Add any additional tracks or configurations here */}
        </MediaOutlet>
        <MediaCommunitySkin />
      </MediaPlayer>
    </div>
  );
}
